import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:5000');

function App() {
  const [username, setUsername] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentView, setCurrentView] = useState('lobby'); // 'lobby' or 'room'
  const [rooms, setRooms] = useState([]);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [newRoomName, setNewRoomName] = useState('');
  const [isMuted, setIsMuted] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [hasVideo, setHasVideo] = useState(false);

  const localAudioRef = useRef(null);
  const remoteAudiosRef = useRef({});
  const peerConnectionsRef = useRef({});
  const localStreamRef = useRef(null);
  const localVideoRef = useRef(null);
  const remoteVideoRefs = useRef({});

  // WebRTC configuration
  const rtcConfig = {
    iceServers: [
      { urls: 'stun:stun.l.google.com:19302' },
      { urls: 'stun:stun1.l.google.com:19302' }
    ]
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(() => {
    // Socket event listeners
    socket.on('connect', () => {
      setIsConnected(true);
    });

    socket.on('disconnect', () => {
      setIsConnected(false);
    });

    socket.on('user-joined', (user) => {
      console.log('User joined app:', user);
    });

    socket.on('rooms-list', (roomsList) => {
      setRooms(roomsList);
    });

    socket.on('room-created', (room) => {
      setRooms(prev => [...prev, room]);
    });

    socket.on('room-updated', (room) => {
      setRooms(prev => prev.map(r => r.id === room.id ? room : r));
    });

    socket.on('joined-room', (data) => {
      setCurrentRoom(data.room);
      setParticipants(data.users);
      setMessages(data.messages);
      setCurrentView('room');
    });

    socket.on('user-joined-room', (user) => {
      setParticipants(prev => [...prev, user]);
      // Initialize WebRTC connection for new user
      initializePeerConnection(user.id);
    });

    socket.on('user-left', (user) => {
      setParticipants(prev => prev.filter(p => p.id !== user.id));
      // Clean up peer connection
      if (peerConnectionsRef.current[user.id]) {
        peerConnectionsRef.current[user.id].close();
        delete peerConnectionsRef.current[user.id];
      }
      if (remoteAudiosRef.current[user.id]) {
        delete remoteAudiosRef.current[user.id];
      }
      if (remoteVideoRefs.current[user.id]) {
        delete remoteVideoRefs.current[user.id];
      }
    });

    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('user-joined');
      socket.off('rooms-list');
      socket.off('room-created');
      socket.off('room-updated');
      socket.off('joined-room');
      socket.off('user-joined-room');
      socket.off('user-left');
    };
  }, []);

  useEffect(() => {
    socket.on('new-message', (message) => {
      setMessages(prev => [...prev, message]);
    });

    return () => {
      socket.off('new-message');
    };
  }, []);

  // WebRTC signaling
  socket.on('offer', async (data) => {
    await handleOffer(data.offer, data.sender);
  });

  socket.on('answer', async (data) => {
    await handleAnswer(data.answer, data.sender);
  });

  socket.on('ice-candidate', async (data) => {
    await handleIceCandidate(data.candidate, data.sender);
  });

  // Initialize audio stream
  const initializeAudio = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      localStreamRef.current = stream;
      if (localAudioRef.current) {
        localAudioRef.current.srcObject = stream;
      }
      return stream;
    } catch (error) {
      console.error('Error accessing microphone:', error);
      alert('Please allow microphone access to use voice chat');
    }
  };

  // Initialize peer connection
  const initializePeerConnection = async (peerId) => {
    if (!localStreamRef.current) {
      await initializeAudio();
    }

    const peerConnection = new RTCPeerConnection(rtcConfig);
    peerConnectionsRef.current[peerId] = peerConnection;

    // Add local stream to peer connection
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach(track => {
        peerConnection.addTrack(track, localStreamRef.current);
      });
    }

    // Handle remote stream
    peerConnection.ontrack = (event) => {
      const remoteStream = event.streams[0];
      if (!remoteAudiosRef.current[peerId]) {
        const audio = new Audio();
        audio.srcObject = remoteStream;
        audio.autoplay = true;
        remoteAudiosRef.current[peerId] = audio;
      }
      if (!remoteVideoRefs.current[peerId]) {
        const video = document.createElement('video');
        video.srcObject = remoteStream;
        video.autoplay = true;
        remoteVideoRefs.current[peerId] = video;
      }
    };

    // Handle ICE candidates
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', {
          candidate: event.candidate,
          target: peerId
        });
      }
    };

    // Create and send offer
    const offer = await peerConnection.createOffer();
    await peerConnection.setLocalDescription(offer);
    socket.emit('offer', { offer, target: peerId });
  };

  // Handle incoming offer
  const handleOffer = async (offer, senderId) => {
    if (!localStreamRef.current) {
      await initializeAudio();
    }

    const peerConnection = new RTCPeerConnection(rtcConfig);
    peerConnectionsRef.current[senderId] = peerConnection;

    // Add local stream
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach(track => {
        peerConnection.addTrack(track, localStreamRef.current);
      });
    }

    // Handle remote stream
    peerConnection.ontrack = (event) => {
      const remoteStream = event.streams[0];
      if (!remoteAudiosRef.current[senderId]) {
        const audio = new Audio();
        audio.srcObject = remoteStream;
        audio.autoplay = true;
        remoteAudiosRef.current[senderId] = audio;
      }
      if (!remoteVideoRefs.current[senderId]) {
        const video = document.createElement('video');
        video.srcObject = remoteStream;
        video.autoplay = true;
        remoteVideoRefs.current[senderId] = video;
      }
    };

    // Handle ICE candidates
    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('ice-candidate', {
          candidate: event.candidate,
          target: senderId
        });
      }
    };

    await peerConnection.setRemoteDescription(offer);
    const answer = await peerConnection.createAnswer();
    await peerConnection.setLocalDescription(answer);
    socket.emit('answer', { answer, target: senderId });
  };

  // Handle incoming answer
  const handleAnswer = async (answer, senderId) => {
    const peerConnection = peerConnectionsRef.current[senderId];
    if (peerConnection) {
      await peerConnection.setRemoteDescription(answer);
    }
  };

  // Handle ICE candidate
  const handleIceCandidate = async (candidate, senderId) => {
    const peerConnection = peerConnectionsRef.current[senderId];
    if (peerConnection) {
      await peerConnection.addIceCandidate(candidate);
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim()) {
      socket.emit('join-app', username.trim());
      setIsLoggedIn(true);
    }
  };

  const handleCreateRoom = (e) => {
    e.preventDefault();
    if (newRoomName.trim()) {
      socket.emit('create-room', newRoomName.trim());
      setNewRoomName('');
    }
  };

  const handleJoinRoom = async (roomId) => {
    await initializeAudio();
    socket.emit('join-room', roomId);
  };

  const handleSendMessage = () => {
    if (newMessage.trim() && currentRoom) {
      const message = {
        text: newMessage,
        username,
        roomId: currentRoom.id
      };
      socket.emit('send-message', message);
      setNewMessage('');
    }
  };

  const handleLeaveRoom = () => {
    // Clean up audio streams and peer connections
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach(track => track.stop());
    }

    Object.values(peerConnectionsRef.current).forEach(pc => pc.close());
    peerConnectionsRef.current = {};
    remoteAudiosRef.current = {};
    remoteVideoRefs.current = {};

    setCurrentView('lobby');
    setCurrentRoom(null);
    setParticipants([]);
    setMessages([]);
  };

  const toggleMute = () => {
    if (localStreamRef.current) {
      const audioTrack = localStreamRef.current.getAudioTracks()[0];
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled;
        setIsMuted(!audioTrack.enabled);
      }
    }
  };

  const toggleVideo = async () => {
    try {
      if (hasVideo) {
        // Stop video
        localStreamRef.current.getVideoTracks().forEach(track => track.stop());
        localVideoRef.current.srcObject = null;
      } else {
        // Start video
        const stream = await navigator.mediaDevices.getUserMedia({ 
          video: true, 
          audio: true 
        });
        localStreamRef.current = stream;
        localVideoRef.current.srcObject = stream;

        // Add video track to existing peer connections
        Object.values(peerConnectionsRef.current).forEach(pc => {
          stream.getTracks().forEach(track => {
            pc.addTrack(track, stream);
          });
        });
      }
      setHasVideo(!hasVideo);
    } catch (err) {
      console.error('Error accessing camera:', err);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="container">
        <div className="card" style={{ maxWidth: '400px', margin: '100px auto' }}>
          <h1 style={{ textAlign: 'center', marginBottom: '24px', color: '#2d3748' }}>
            Voice Chat App
          </h1>
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="input"
              style={{ marginBottom: '16px' }}
              required
            />
            <button type="submit" className="btn" style={{ width: '100%' }}>
              Join Chat
            </button>
          </form>
          <div style={{ textAlign: 'center', marginTop: '16px', color: isConnected ? '#48bb78' : '#e53e3e' }}>
            {isConnected ? '游릭 Connected' : '游댮 Disconnected'}
          </div>
        </div>
      </div>
    );
  }

  if (currentView === 'lobby') {
    return (
      <div className="container">
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
            <h1 style={{ color: '#2d3748' }}>Welcome, {username}!</h1>
            <div style={{ color: isConnected ? '#48bb78' : '#e53e3e' }}>
              {isConnected ? '游릭 Connected' : '游댮 Disconnected'}
            </div>
          </div>

          <form onSubmit={handleCreateRoom} style={{ marginBottom: '24px' }}>
            <div style={{ display: 'flex', gap: '12px' }}>
              <input
                type="text"
                placeholder="Room name"
                value={newRoomName}
                onChange={(e) => setNewRoomName(e.target.value)}
                className="input"
                style={{ flex: 1 }}
                required
              />
              <button type="submit" className="btn">
                Create Room
              </button>
            </div>
          </form>

          <h2 style={{ marginBottom: '16px', color: '#4a5568' }}>Available Rooms</h2>
          {rooms.length === 0 ? (
            <div className="loading">No rooms available. Create one to get started!</div>
          ) : (
            <div className="room-grid">
              {rooms.map(room => (
                <div key={room.id} className="room-card" onClick={() => handleJoinRoom(room.id)}>
                  <h3 style={{ marginBottom: '8px', color: '#2d3748' }}>{room.name}</h3>
                  <p style={{ color: '#718096', marginBottom: '8px' }}>
                    Created by: {room.createdBy}
                  </p>
                  <p style={{ color: '#667eea', fontWeight: '500' }}>
                    游논 {room.userCount} participant{room.userCount !== 1 ? 's' : ''}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="chat-container">
      {/* Left sidebar - Chat */}
      <div className="chat-sidebar">
        <div className="messages">
          {messages.map((msg, i) => (
            <div 
              key={i} 
              className={`message ${msg.username === username ? 'sent' : 'received'}`}
              style={{ 
                display: 'flex', 
                justifyContent: msg.username === username ? 'flex-end' : 'flex-start', 
                marginBottom: '16px' 
              }}
            >
              <div className="message-text" style={{ 
                backgroundColor: msg.username === username ? '#48bb78' : '#e2e8f0', 
                color: msg.username === username ? '#fff' : '#2d3748', 
                padding: '8px', 
                borderRadius: '8px' 
              }}>
                {msg.text}
              </div>
              <div className="message-time" style={{ 
                fontSize: '12px', 
                color: '#718096', 
                marginLeft: msg.username === username ? '8px' : '0', 
                marginRight: msg.username === username ? '0' : '8px' 
              }}>
                {new Date(msg.timestamp).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
              </div>
            </div>
          ))}
        </div>

        <div className="message-input">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter') {
                handleSendMessage();
              }
            }}
            placeholder="Type a message..."
          />
          <button onClick={handleSendMessage}>Send</button>
        </div>
      </div>

      {/* Main content - Videos */}
      <div className="video-main">
        <div className="video-grid">
          <video ref={localVideoRef} autoPlay muted className="local-video" />
          {Object.entries(remoteVideoRefs.current).map(([id, video]) => (
            <video 
              key={id} 
              ref={ref => ref && (ref.srcObject = video.srcObject)} 
              autoPlay 
              className="remote-video"
            />
          ))}
        </div>

        <div className="video-controls">
          <button
            onClick={toggleMute}
            className={`mute-btn ${isMuted ? 'active' : ''}`}
            title={isMuted ? 'Unmute' : 'Mute'}
          >
            {isMuted ? '游댆' : '游꿗'}
          </button>
          <button
            onClick={toggleVideo}
            className={`video-btn ${hasVideo ? 'active' : ''}`}
            title={hasVideo ? 'Stop Video' : 'Start Video'}
          >
            {hasVideo ? '游닟' : '游닠'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
